#set ($dateFormat = "MM/dd/yyyy")
##
##
#macro (__filters_dateRange $field_name $dateStart $dateEnd)
(% class="date-range" %)(((
#__filters_dateField ("$services.localization.render('phenotips.databaseFilters.after.label') " "${field_name}/after" $dateStart)

#__filters_dateField ("$services.localization.render('phenotips.databaseFilters.before.label') " "${field_name}/before" $dateEnd)
)))
#end
##
#macro (__filters_dateField $label $field_name $value)
{{html clean="false"}}<label>$label <input class="xwiki-date" type="text" alt="#if($value)#formatdateISO($value)#end" value="$!xwiki.formatDate($value, $definedFormat)" name="${field_name}" title="${dateFormat}" /></label>{{/html}}
#end

#macro(__filters_display $cssClass)
(% class="filters $!cssClass" %)
* $services.localization.render('phenotips.databaseFilters.externalID') {{html clean=false}}<input type="text" name="external_id" value=""/>{{/html}}
* $services.localization.render('phenotips.databaseFilters.referrer') {{html clean=false}}<input type="text" class="suggestUsers multipleSelection" name="doc.creator" value=""/>{{/html}}
* $services.localization.render('phenotips.databaseFilters.author') {{html clean=false}}<input type="text" class="suggestUsers multipleSelection" name="doc.author" value=""/>{{/html}}
* $services.localization.render('phenotips.databaseFilters.creationDate') #__filters_dateRange("doc.creationDate")
* $services.localization.render('phenotips.databaseFilters.modificationDate') #__filters_dateRange("doc.date")
#end
{{/velocity}}
